#include <stdio.h>
#include <string.h>
#include <inttypes.h>

/* function to find the greatest possible product of 13 consecutive numbers
   in the string */

int main(void)
{
	char *str = "";
	uint64_t i = 0;
	unsigned int j;
	uint64_t k;
	unsigned int n = 13;
	char *str_end = str + 1000 - n;
	char *str_count = str;
	char *str_c_c;

	while (str_count <= str_end)
	{
		str_c_c = str_count;
		k = 1;
		for (j = 0; j < n; j++)
		{
			k *= *str_c_c - '0';

			str_c_c++;
		}

		if (k > i)
			i = k;

		str_count++;
	}

	printf("%" PRId64 "\n", i);

	return (0);
}
